mapper.select:
  		
  		 
  		select *
		from(select rownum as rn, qna_no, pno, uno, sno, b_history, q_title, q_content, q_img_name, q_img_ext, q_uploadpath, q_fileloca, user_id, regdate
		     from(select * 
		         from d_qna where pno = #{pno}
		         order by qna_no desc
		         )
		) where rn > (#{cri.pageNum}-1) * #{cri.amount} and rn <= #{cri.pageNum} * #{cri.amount}

mapper.update:
  		update d_qna set 
	  		uno = #{uno},
	  		b_history = #{b_history},
	  		q_title = #{q_title},
	  		q_content = #{q_content}
	  		where qna_no = #{qna_no}
