mapper.select:
  		
	  	select *
	  	from(select rownum as rn, review_no, r_title, r_content, user_id, regdate, starcount
	  		from(select * 
		  		from d_review where pno = #{pno}
		  		order by review_no desc ))
		where rn > (#{cri.pageNum}-1) * #{cri.amount} and rn <= #{cri.pageNum} * #{cri.amount}
